/* eslint-disable react-hooks/exhaustive-deps */
import React, { useState, useEffect } from 'react'
import axios from 'axios';
import jwt_decode from "jwt-decode";
import { useNavigate } from 'react-router-dom';
import Navbar from './Navbar';
import {Card,CardBody,CardHeader,CardFooter,Button,Box,Layer} from "grommet";

 
export const Dashboard = () => {
    const [name, setName] = useState('');
    const [token, setToken] = useState('');
    const [expire, setExpire] = useState('');
    const [users, setUsers] = useState([]);
    const [accessToken,setAccessToken] = useState('');
    const navigate = useNavigate();
    
    useEffect(() => {
        fitbitToken();
        registerFitbit();
        //refreshToken();
        //getUsers();
        
        // axios.get('fitbit_register',axiosConfig).then((res) =>
        //     {
        //       console.log(res.data);
        //     });
    }, []);
 
    
    const registerFitbit = async () => {
        let axiosConfig = {
            headers: {
                'Content-Type': 'application/json;charset=UTF-8',
                "Access-Control-Allow-Origin": "*",
                "Authorization": `Bearer eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpYXQiOjE2Njg1Nzk1MzQsImV4cCI6MTY2ODU4MDQzNCwianRpIjoiZjU0MmFhMzYtNjgyNy00ZmJjLTg0NmQtMDFlOTlhZWNjM2VjIiwiaWQiOiJhcmtvLnBhbDIwMDBAZ21haWwuY29tIiwicmxzIjoiIiwicmZfZXhwIjoxNjcxMTcxNTM0fQ.DwI11yg0ISj6ZSMQsYRrha7YPURCVe9qvA5RMnjk2hk`,
            }
          };
        try {
            const response = await axios.get("fitbit_register",axiosConfig);
            //setAccessToken(response.data.accessToken);
            console.log(response);
        } catch (error) {
            console.log(error);
        }
    }
    const fitbitToken = async () => {
        try {
            const response = await axios.post("login",{
                email_id: "arko.pal2000@gmail.com",
                password: "12345"
            });
            //setAccessToken(response.data.accessToken);
            console.log(response.data.access_token);
            setAccessToken(response.data.access_token);
        } catch (error) {
            console.log(error);
        }
        }
    
    const refreshToken = async () => {
        try {
            const response = await axios.get('http://localhost:5000/token');
            // console.log('hello');
            // console.log(response);
            setToken(response.data.accessToken);
            const decoded = jwt_decode(response.data.accessToken);
            setName(decoded.name);
            setExpire(decoded.exp);
        } catch (error) {
            if (error.response) {
                navigate("/");
            }
        }
    }
 
    const axiosJWT = axios.create();
 
    axiosJWT.interceptors.request.use(async (config) => {
        const currentDate = new Date();
        if (expire * 1000 < currentDate.getTime()) {
            const response = await axios.get('http://localhost:5000/token');
            config.headers.Authorization = `Bearer ${response.data.accessToken}`;
            setToken(response.data.accessToken);
            const decoded = jwt_decode(response.data.accessToken);
            setName(decoded.name);
            setExpire(decoded.exp);
        }
        return config;
    }, (error) => {
        return Promise.reject(error);
    });
 
    // const getUsers = async () => {
    //     const response = await axiosJWT.get('http://localhost:5000/users', {
    //         headers: {
    //             Authorization: `Bearer ${token}`
    //         }
    //     });
    //     setUsers(response.data);
    //}
    const [show,setShow] = useState();
    return (
        <Box>
            <Box>
                <Navbar/>
            </Box>
            <Box>
                <Button type='submit' primary label='Add data' onClick={setToggle(true)}/>
            </Box>

            <Box>
            <Box>
            <Button label="show" onClick={() => setShow(true)} />
                {show && (
                <Layer
                    onEsc={() => setShow(false)}
                    onClickOutside={() => setShow(false)}
                >
            <Button label="close" onClick={() => setShow(false)} />
        </Layer>
      )}
    </Box>
            </Box>
        </Box>
        
    )
}

export default Dashboard
